import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as e,d as r}from"./app.f3ba2c94.js";const i={},o=r('<p><em>\u591F\u5403\u5417\uFF1F\u8FD8\u80FD\u4E70\u5230\u4E1C\u897F\u5417\uFF1F</em></p><p>\u6240\u6709\u4EBA\u90FD\u95EE\u6211\u8FD9\u53E5\u8BDD\uFF0C\u56E0\u4E3A\u4F60\u5728\u4E0A\u6D77\u3002\u5728\u8206\u8BBA\u4E2D\uFF0C\u4E0A\u6D77\u4F3C\u4E4E\u56DE\u5230 100 \u5E74\u524D\u3002\u7B2C\u4E00\u6B21\u611F\u53D7\u5230\uFF0C\u4EC0\u4E48\u53EB\u300C\u707E\u533A\u4EBA\u6C11\u300D\u3002</p><p>\u4F46\u771F\u662F\u8FD9\u6837\u5417\uFF1F\u6211\u771F\u7684\u751F\u6D3B\u5728\u300C\u4E0A\u6D77\u300D\u5417\uFF1F</p><h2 id="\u4EB2\u8EAB\u4F53\u9A8C-vs-\u7F51\u4E0A\u300C\u4E0A\u6D77\u300D" tabindex="-1"><a class="header-anchor" href="#\u4EB2\u8EAB\u4F53\u9A8C-vs-\u7F51\u4E0A\u300C\u4E0A\u6D77\u300D" aria-hidden="true">#</a> \u4EB2\u8EAB\u4F53\u9A8C VS \u7F51\u4E0A\u300C\u4E0A\u6D77\u300D</h2><p>3 \u6708 28 \u65E5\uFF0C\u65B0\u95FB\u516C\u544A\u6D66\u897F\u5C06\u4E8E 4 \u6708 1 \u65E5\u5F00\u59CB\u5C01\u63A7 4 \u5929\u3002\u63A5\u5230\u901A\u77E5\u540E\uFF0C\u6211\u8FDB\u884C\u4E86\u5927\u91C7\u8D2D\uFF0C\u628A\u51B0\u7BB1\u585E\u6EE1\u30023 \u6708 30 \u65E5\uFF0C\u7269\u4E1A\u901A\u77E5\u5C0F\u533A\u63D0\u524D\u5C01\u63A7\uFF0C\u5C31\u6B64\u8FDB\u5165\u5B85\u5BB6\u72B6\u6001\u3002\u6B64\u65F6\uFF0C\u7269\u8D44\u50A8\u5907\u4E00\u5468\u3002</p><p>\u8FC7\u4E86\u51E0\u5929\uFF0C\u4E0A\u6D77\u75AB\u60C5\u5F7B\u5E95\u7206\u53D1\uFF0C\u5C01\u63A7\u65F6\u95F4\u88AB\u5EF6\u957F\u3002\u7269\u8D44\u53EA\u51FA\u4E0D\u8FDB\uFF0C\u6211\u4E5F\u8FDB\u5165\u7126\u8651\u72B6\u6001\uFF0C\u5F00\u59CB\u7EBF\u4E0A\u62A2\u8D2D\u3002\u57FA\u672C\u6BCF\u5929\u90FD\u80FD\u62A2\u5230\uFF0C\u7269\u8D44\u53EF\u7EF4\u6301\u4E24\u5468\u3002</p><p>4 \u6708 5 \u65E5\uFF0C\u5C0F\u533A\u56E2\u8D2D\u7FA4\u5174\u8D77\uFF0C\u5F00\u59CB\u5927\u91CF\u91C7\u8D2D\uFF0C\u7269\u8D44\u53EF\u7EF4\u6301\u4E09\u5468\u3002\u540C\u65F6\uFF0C\u8857\u9053\u5F00\u59CB\u5C0F\u91CF\u53D1\u653E\u7269\u8D44\u3002</p><p>4 \u6708 10 \u65E5\u540E\uFF0C\u8857\u9053\u5F00\u59CB\u52A0\u5927\u7269\u8D44\u53D1\u653E\u91CF\uFF0C\u751A\u81F3\u5305\u62EC\u5395\u7EB8\u3001\u6C90\u6D74\u9732\u3002\u6211\u5B8C\u5168\u6CA1\u60F3\u5230\uFF0C\u8857\u9053\u53D1\u8FD9\u4E48\u591A\u7269\u8D44\u3002\u5176\u4E2D\u7684\u852C\u83DC\u65E0\u6CD5\u957F\u65F6\u95F4\u4FDD\u5B58\uFF0C\u6211\u5F00\u59CB\u81EA\u5B66\u505A\u9171\u83DC\u548C\u852C\u83DC\u997C\u3002\u7269\u8D44\u53EF\u7EF4\u6301\u4E00\u4E2A\u6708\u3002</p><p><img src="http://tc.seoipo.com/2022-04-20-21-43-24.png" alt="\u8857\u9053\u53D1\u653E\u7684\u90E8\u5206\u7269\u8D44" loading="lazy"></p><p>\u6B64\u65F6\uFF0C\u7F51\u4E0A\u7684\u4E0A\u6D77\u5219\u662F\u53E6\u4E00\u4E2A\u4E16\u754C\u3002\u201C\u6DF7\u4E71\uFF0C\u5403\u4E0D\u4E86\u996D\uFF0C\u5FEB\u997F\u6B7B\uFF1F\u201D</p><p>\u56E0\u4E3A\u8FD9\u4E9B\u8C23\u4F20\uFF0C\u5BB6\u4EBA\u3001\u670B\u53CB\u3001\u751A\u81F3\u7F51\u53CB\u90FD\u5F00\u59CB\u95EE\u6587\u7AE0\u5F00\u5934\u7684\u8BDD\u3002\u300C\u591F\u5403\u5417\uFF1F\u8FD8\u80FD\u4E70\u5230\u4E1C\u897F\u5417\uFF1F\u300D</p><h2 id="\u5E78\u5B58\u8005\u504F\u5DEE" tabindex="-1"><a class="header-anchor" href="#\u5E78\u5B58\u8005\u504F\u5DEE" aria-hidden="true">#</a> \u5E78\u5B58\u8005\u504F\u5DEE</h2><p>\u96BE\u9053\u7269\u8D44\u5145\u8DB3\u53EA\u662F\u6211\u4E2A\u4EBA\u7684\u611F\u53D7\uFF0C\u5176\u4ED6\u4EBA\u90FD\u5904\u4E8E\u7269\u8D44\u77ED\u7F3A\u7684\u72B6\u6001\u5417\uFF1F</p><p>\u63A5\u7740\uFF0C\u6211\u95EE\u4E86\u540C\u5728\u4E0A\u6D77\u7684\u670B\u53CB\u3002\u5927\u5BB6\u53CD\u9988\uFF0C\u7269\u8D44\u90FD\u6CA1\u6211\u591A\uFF0C\u4F46\u57FA\u672C\u7684\u751F\u6D3B\u662F\u80FD\u6EE1\u8DB3\uFF0C\u6700\u5C11\u80FD\u5403\u9971\u3002</p><p>\u90A3\u4E3A\u4EC0\u4E48\u7F51\u4E0A\u5173\u4E8E\u4E0A\u6D77\u7684\u73B0\u72B6\u90FD\u662F\u300C\u6CA1\u4EBA\u7BA1\uFF0C\u6CA1\u5403\u7684\u300D\uFF1F</p><p>\u6211\u5F00\u59CB\u5927\u91CF\u67E5\u770B\u4E0A\u6D77\u75AB\u60C5\u7684\u76F8\u5173\u8D44\u8BAF\uFF0C\u53D1\u73B0\u662F\u300C\u5E78\u5B58\u8005\u504F\u5DEE\u300D\u653E\u5927\u4E86\u4E0A\u6D77\u7684\u7269\u8D44\u77ED\u7F3A\u3002</p><blockquote><p>\u5E78\u5B58\u8005\u504F\u5DEE\uFF08Survivorship Bias\uFF09\uFF0C\u4EBA\u4EEC\u6240\u83B7\u5F97\u7684\u6709\u4E9B\u4FE1\u606F\u53EF\u80FD\u4F1A\u56E0\u4E3A\u4FE1\u606F\u7684\u63D0\u4F9B\u65B9\u7684\u532E\u4E4F\uFF08\u53EA\u6709\u90A3\u4E9B\u201C\u5E78\u5B58\u201D\u4E0B\u6765\u7684\u4EBA\u4F1A\u63D0\u4F9B\u4FE1\u606F\uFF0C\u201C\u6B7B\u4EBA\u4E0D\u4F1A\u8BF4\u8BDD\u201D\uFF09\uFF0C\u800C\u4F7F\u5F97\u7531\u8FD9\u4E9B\u4FE1\u606F\u5F97\u51FA\u7684\u7ED3\u8BBA\u7F3A\u4E4F\u666E\u9002\u6027\u3002\u4F46\u4EBA\u4EEC\u5F80\u5F80\u4F1A\u5FFD\u89C6\u8FD9\u4E00\u70B9\uFF0C\u5FFD\u89C6\u800C\u628A\u83B7\u5F97\u7684\u4FE1\u606F\u8FC7\u5EA6\u6982\u62EC\u3002</p></blockquote><p>4 \u6708\u5F00\u59CB\uFF0C\u4E0A\u6D77 2500 \u4E07\u4EBA\u5168\u90E8\u8FDB\u5165\u5C01\u63A7\u72B6\u6001\u3002\u8FD9\u4E00\u5E9E\u5927\u7684\u4F9B\u7ED9\u9700\u6C42\u4E00\u4E0B\u5B50\u7838\u5230\u8857\u9053\u793E\u533A\u5934\u4E0A\uFF0C\u57FA\u5C42\u7EC4\u7EC7\u5173\u7CFB\u63A5\u8FD1\u5D29\u6E83\u3002</p><p>\u5927\u591A\u6570\u7269\u8D44\u50A8\u5907\u5145\u8DB3\uFF0C\u80FD\u7F51\u4E0A\u62A2\u83DC\u7684\u4EBA\uFF0C\u5B89\u9759\u5730\u5728\u5BB6\u5C01\u63A7\uFF0C\u81EA\u7136\u4E5F\u4E0D\u4F1A\u5728\u7F51\u4E0A\u53D1\u8A00\u3002</p><p>\u53EF\u662F\uFF0C\u8001\u4EBA\u7B49\u5F31\u52BF\u7FA4\u4F53\u5927\u90FD\u672A\u63A5\u5230\u7269\u8D44\u50A8\u5907\u7684\u6D88\u606F\uFF0C\u4E5F\u7F3A\u5C11\u7EBF\u4E0A\u62A2\u8D2D\u624B\u6BB5\uFF0C\u53EA\u80FD\u901A\u8FC7\u5C3D\u53EF\u80FD\u591A\u7684\u6E20\u9053\u53D1\u58F0\u6765\u6C42\u6551\u3002</p><p>2500 \u4E07\u4EBA\u5373\u4F7F\u53EA\u6709\u4E07\u5206\u4E4B\u4E00\u7684\u9057\u6F0F\uFF0C\u4E5F\u6709 2500 \u4EBA\u9047\u5230\u56F0\u96BE\u3002</p><p>\u5F53\u8FD9\u6279\u4EBA\u5F00\u59CB\u53D1\u58F0\uFF0C\u5C31\u5F62\u6210\u4E86\u300C\u9965\u997F\u7684\u4E0A\u6D77\u300D\u5F62\u8C61\uFF0C\u8FD9\u4E5F\u5C31\u662F\u4E3A\u4EC0\u4E48\u8206\u8BBA\u4E2D\u7684\u4E0A\u6D77\u4E0E\u5B9E\u9645\u751F\u6D3B\u5B8C\u5168\u4E0D\u540C\u7684\u539F\u56E0\u3002</p><p>\u90A3\u4E48\uFF0C\u6392\u9664\u6389\u300C\u5E78\u5B58\u8005\u504F\u5DEE\u300D\u56E0\u7D20\uFF0C\u4E0A\u6D77\u771F\u5B9E\u5728\u53D1\u751F\u4EC0\u4E48\uFF1F</p><h2 id="\u4E0A\u6D77\u7684\u56F0\u96BE" tabindex="-1"><a class="header-anchor" href="#\u4E0A\u6D77\u7684\u56F0\u96BE" aria-hidden="true">#</a> \u4E0A\u6D77\u7684\u56F0\u96BE</h2><blockquote><p>\u65F6\u4EE3\u4E2D\u7684\u4E00\u7C92\u7070\uFF0C\u843D\u5728\u4E2A\u4EBA\u5934\u4E0A\uFF0C\u5C31\u662F\u4E00\u5EA7\u5C71\u3002</p></blockquote><p>\u9664\u4E86\u521D\u671F\u7684\u7269\u8D44\u532E\u4E4F\uFF0C\u4E0A\u6D77\u9762\u4E34\u7684\u56F0\u96BE\u8FD8\u8FDC\u4E0D\u6B62\u6B64\uFF0C\u6838\u9178\u6DF7\u4E71\u3001\u653F\u7B56\u591A\u53D8\u3001\u98DF\u54C1\u5B89\u5168\u3001\u7269\u4EF7\u9AD8\u4F01\u3001\u5C31\u533B\u56F0\u96BE\uFF0C\u90FD\u662F\u6446\u5728\u4E0A\u6D77\u4EBA\u9762\u524D\u7684\u5927\u5C71\u3002</p><p>\u5F53\u8FD9\u4E9B\u6D88\u606F\u96C6\u4E2D\u91CA\u653E\u540E\uFF0C\u5373\u4F7F\u4E0D\u662F\u5F53\u4E8B\u4EBA\u4E5F\u611F\u5230\u6781\u5EA6\u538B\u6291\u3002\u4F30\u8BA1\u89E3\u9664\u5C01\u63A7\u540E\uFF0C\u5927\u5BB6\u90FD\u5F97\u53BB\u5FC3\u7406\u54A8\u8BE2\u4E86\u3002</p><p>\u5C45\u5BB6\u9694\u79BB\u524D\uFF0C\u6211\u89C9\u5F97\u9694\u79BB\u4E5F\u4E0D\u9519\uFF0C\u6709\u5403\u6709\u559D\uFF0C\u8FD8\u767D\u62FF\u5DE5\u8D44\u3002\u4E4B\u524D\uFF0C\u6211\u751A\u81F3\u8FD8\u5199\u4E86\u4E00\u7BC7\u6587\u7AE0\uFF0C\u8BA4\u4E3A\u6D66\u4E1C\u5C01\u63A7\u653E\u5047\uFF0C\u800C\u6D66\u897F\u5C31\u5F97\u4F4F\u5728\u516C\u53F8\u52A0\u73ED\uFF0C\u4E0D\u516C\u5E73\u3002</p><p>\u4F46\u662F\uFF0C\u5F53\u4EB2\u8EAB\u4F53\u9A8C\u5230\u957F\u65F6\u95F4\u9694\u79BB\uFF0C\u624D\u77E5\u9053\u5931\u53BB\u81EA\u7531\u7684\u4EE3\u4EF7\u9AD8\u4E8E\u4E00\u5207\u3002</p><p><strong>\u4E0A\u6D77\u771F\u6B63\u9762\u4E34\u7684\u56F0\u96BE\uFF0C\u662F\u957F\u65F6\u95F4\u9694\u79BB\u5E26\u6765\u7684\u538B\u6291\u60C5\u7EEA</strong>\u3002\u8FD9\u662F\u6BCF\u4E2A\u4E0A\u6D77\u5C45\u6C11\u9762\u4E34\u7684\u6700\u5927\u56F0\u96BE\u3002\u7269\u8D44\u5DF2\u7ECF\u9010\u6E10\u5145\u88D5\uFF0C\u56F0\u96BE\u4E5F\u9010\u6E10\u514B\u670D\uFF0C\u4F46\u538B\u6291\u7684\u60C5\u7EEA\u5374\u4E00\u76F4\u5728\u7D27\u7EF7\u3002</p><p>\u4E1C\u65B9\u53F0\u5F53\u65F6\u641E\u6297\u75AB\u665A\u4F1A\uFF0C\u60F3\u5FC5\u4E5F\u662F\u60F3\u7ED9\u957F\u65F6\u95F4\u5C01\u63A7\u5728\u5BB6\u91CC\u7684\u6211\u4EEC\u5E26\u6765\u4E00\u4E9B\u632F\u594B\u7684\u60C5\u7EEA\u5427\u3002</p><p>\u4F46\u5B83\u6CA1\u7406\u89E3\uFF0C\u6211\u4EEC\u9700\u8981\u7684\u4E0D\u662F\u665A\u4F1A\uFF0C\u800C\u662F\u771F\u6B63\u7684\u597D\u6D88\u606F - <strong>\u75AB\u60C5\u7ED3\u675F</strong>\u3002</p><h2 id="\u75AB\u60C5\u4E2D\u7684\u5E0C\u671B" tabindex="-1"><a class="header-anchor" href="#\u75AB\u60C5\u4E2D\u7684\u5E0C\u671B" aria-hidden="true">#</a> \u75AB\u60C5\u4E2D\u7684\u5E0C\u671B</h2><p>\u4E3A\u4E86\u5199\u8FD9\u7BC7\u6587\u7AE0\uFF0C\u770B\u4E86\u8BB8\u591A\u4E0A\u6D77\u75AB\u60C5\u6240\u53D1\u751F\u7684\u60B2\u5267\uFF0C\u6211\u7684\u60C5\u7EEA\u4E5F\u5B8C\u5168\u8FDB\u5165\u4F4E\u8C37\u3002\u53EF\u60F3\u800C\u77E5\uFF0C\u5F53\u4E8B\u4EBA\u5F97\u6709\u591A\u4E48\u75DB\u82E6\u3002</p><p>\u4F46\u5BF9\u666E\u901A\u4EBA\uFF0C\u5E0C\u671B\u5927\u5BB6\u522B\u53BB\u5173\u6CE8\uFF0C\u8FD9\u4E9B\u574F\u6D88\u606F\u771F\u7684\u4F1A\u8BA9\u4EBA\u6291\u90C1\u3002\u5F53\u8FD9\u80A1\u538B\u6291\u60C5\u7EEA\u6765\u4E34\u65F6\uFF0C\u6211\u771F\u5207\u5730\u611F\u53D7\u5230\u300C\u4EC0\u4E48\u662F\u707E\u6C11\u300D\u3002\u8FD9\u4E0D\u662F\u7269\u8D28\u4E0A\u7684\u532E\u4E4F\uFF0C\u800C\u662F\u7CBE\u795E\u4E0A\u7684\u538B\u6291\uFF0C\u8FD9\u7ED9\u4EBA\u7684\u521B\u4F24\u66F4\u5927\u3002\u5F00\u59CB\u7406\u89E3\uFF0C\u88AB\u7F51\u66B4\u7684\u4E0A\u6D77\u5973\u513F\u4E3A\u4EC0\u4E48\u8981\u81EA\u6740\u4E86\uFF0C\u60F3\u5FC5\u662F\u538B\u6291\u81F3\u6781\u53C8\u78B0\u4E0A\u7F51\u66B4\u800C\u65E0\u6CD5\u627F\u53D7\u3002</p><p>\u5E0C\u671B\u6B67\u89C6\u4E0A\u6D77\u7684\u6BB5\u5B50\u80FD\u5C11\u4E9B\uFF0C\u538B\u6291\u7684\u60C5\u7EEA\u4E2D\u5DF2\u7ECF\u5230\u4E86\u4E34\u754C\u3002</p><p>\u5E0C\u671B\u6B63\u80FD\u91CF\u7684\u6D88\u606F\u80FD\u591A\u4E9B\uFF0C\u6211\u4EEC\u6B64\u523B\u771F\u7684\u5F88\u9700\u8981\u4E00\u70B9\u6FC0\u52B1\u3002</p><p>\u5E0C\u671B\u4E0A\u6D77\u75AB\u60C5\u80FD\u65E9\u70B9\u7ED3\u675F\uFF0C\u8BA9\u751F\u6D3B\u6062\u590D\u6B63\u5E38\u3002</p><p>\u4E5F\u5E0C\u671B\u75AB\u60C5\u7ED3\u675F\u540E\uFF0C\u4E0A\u6D77\u80FD\u597D\u597D\u590D\u76D8\uFF0C\u8BB0\u4F4F\u6559\u8BAD\uFF0C\u4E0D\u8981\u8BA9\u5386\u53F2\u91CD\u6F14\u3002</p>',39),t=[o];function s(h,n){return a(),e("div",null,t)}var _=p(i,[["render",s],["__file","2022-04-20-survivorship_bias_in_shanghai_2022.html.vue"]]);export{_ as default};
